import unittest
import json

# test baidu.py
from ocs.baidu import fetch_token, _request
from urllib.parse import urlencode, quote_plus


class TestBaidu(unittest.TestCase):
    def test_image2text(self):
        """
        Test that it can sum a list of integers
        """
        image = ''
        r = _request(urlencode({'image': image}))

        self.assertTrue("v9I8" in json.loads(r)['words_result'][0]['words'])


if __name__ == '__main__':
    unittest.main()
